import java.util.Scanner; public class HelloWorld{      static String name;      static int l,i;      public static void main(String []args){         Scanner s=new Scanner(System.in);         System.out.println("enter the string");         name=s.nextLine();         l=name.length();         int n=l-1;         char[] temp=new char[l];         for(i=0;i<l;i++)         {             temp[i]=name.charAt(n);             n=n-1;         }         System.out.println(temp);       } }
